// ë°©ë²• 3: ê³ ì • ê¸¸ì´ ê¸°ì¤€ìœ¼ë¡œ ë‚˜ëˆ„ê¸°
if (splitSentences.length !== 6) {
  console.log('ë°©ë²• 3 ì‚¬ìš©: ê¸¸ì´ ê¸°ì¤€ ë¶„í• ');
  const avgLength = Math.ceil(narrationText.length / 6);
  const parts = [];
