let logoData = null;

// ê° ì…ë ¥ ë°ì´í„° ì²˜ë¦¬
inputItems.forEach((item, index) => {
  const data = item.json;

  console.log(`ğŸ“‹ ì…ë ¥ ${index}: ${data.name || 'unnamed'} (${data.mimeType || data.kind || 'unknown'})`);

  // ë¹„ë””ì˜¤ íŒŒì¼ (ë¡œê³ ê°€ ì•„ë‹Œ ê²½ìš°)
