// === ğŸ”§ ìˆ˜ì •ëœ matchedSegments ìƒì„± ===
function createMatchedSegments() {
  if (!wordSubtitleData || !wordSubtitleData.segmentTimings) {
    console.log('âŒ ìë§‰ ë°ì´í„° ì—†ìŒ - matchedSegments ìƒì„± ë¶ˆê°€');
    return [];
  }

  const segments = [];
  const originalTimings = wordSubtitleData.segmentTimings;

  console.log('ğŸ”§ matchedSegments ìƒì„± ì¤‘...');
  console.log('ğŸ“ ì›ë³¸ ìë§‰ íƒ€ì´ë°:');
  originalTimings.forEach((timing, i) => {
    console.log(`  ì„¸ê·¸ë¨¼íŠ¸ ${i}: ${timing.startTime.toFixed(2)}~${timing.endTime.toFixed(2)}ì´ˆ (${timing.duration.toFixed(2)}ì´ˆ)`);
  });

  // ğŸ†• íŒŒì¼ëª…ì—ì„œ ìˆ«ì ì¶”ì¶œí•˜ëŠ” í•¨ìˆ˜ (ìˆ˜ì •ë¨)
  function extractNumber(filename) {
    // íŒŒì¼ëª…ì—ì„œ _XX_ íŒ¨í„´ì˜ ìˆ«ì ì¶”ì¶œ (ì˜ˆ: "YYYY08Aug 5, 2025_01_video1.mp4" â†’ 1)
    const match = filename.match(/_(\d+)_/);
    if (match) {
      return parseInt(match[1], 10);
    }
    // ìˆ«ìê°€ ì—†ìœ¼ë©´ 100 ë°˜í™˜ (999ëŠ” ë„ˆë¬´ í° ê°’ì´ë¼ ë¬¸ì œ ë°œìƒ)
    return 100;
  }

  // ğŸ¯ ë¹„ë””ì˜¤ì™€ ì´ë¯¸ì§€ íŒŒì¼ ì •ë ¬
  const sortedVideoFiles = videoFiles.sort((a, b) => {
    const numA = extractNumber(a.name);
    const numB = extractNumber(b.name);
    console.log(`  ë¹„êµ: ${a.name}(${numA}) vs ${b.name}(${numB})`);
    return numA - numB;
  });

  const sortedImageFiles = imageFiles.sort((a, b) => {
    const numA = extractNumber(a.name);
    const numB = extractNumber(b.name);
    console.log(`  ë¹„êµ: ${a.name}(${numA}) vs ${b.name}(${numB})`);
    return numA - numB;
  });

  // ë¹„ë””ì˜¤ë¥¼ ë¨¼ì € ë°°ì¹˜í•˜ê³ , ê·¸ ë‹¤ìŒì— ì´ë¯¸ì§€ ë°°ì¹˜
  const allMediaFiles = [...sortedVideoFiles, ...sortedImageFiles];

  console.log('ğŸ“ ë„˜ë²„ë§ + ë¹„ë””ì˜¤ ìš°ì„ ìˆœìœ„ë¡œ ì •ë ¬ëœ ë¯¸ë””ì–´ íŒŒì¼:');
  allMediaFiles.forEach((file, i) => {
    const num = extractNumber(file.name);
    const type = file.mimeType === 'video/mp4' ? 'VIDEO' : 'IMAGE';
    console.log(`  ì„¸ê·¸ë¨¼íŠ¸ ${i}: ${file.name} (ë²ˆí˜¸: ${num}, íƒ€ì…: ${type})`);
  });

  // ì„¸ê·¸ë¨¼íŠ¸ 0~5: ì •ë ¬ëœ ë¹„ë””ì˜¤ì™€ ì´ë¯¸ì§€ë¥¼ ìˆœì„œëŒ€ë¡œ ë°°ì¹˜
  originalTimings.forEach((originalTiming, i) => {
    if (i < allMediaFiles.length) {
      const mediaFile = allMediaFiles[i];
      const isVideo = mediaFile.mimeType === 'video/mp4';

      const segment = {
        segmentId: i,
        type: isVideo ? "generated_video" : "generated_image",
        id: mediaFile.id,
        name: mediaFile.name,
        mimeType: mediaFile.mimeType,
        url: `https://drive.google.com/uc?id=${mediaFile.id}&export=download`,
        source: mediaFile.source,
        timing: {
          startTime: originalTiming.startTime,
          endTime: originalTiming.endTime,
          duration: originalTiming.duration
        },
        text: originalTiming.text
      };

      segments.push(segment);
      console.log(`  âœ… ì„¸ê·¸ë¨¼íŠ¸ ${i} ìƒì„±: ${mediaFile.name}`);
    }
  });

  // ìë§‰ ê¸°ì¤€ ì´ ì‹œê°„ (ì›ë³¸ ìë§‰ì˜ ë§ˆì§€ë§‰ ì„¸ê·¸ë¨¼íŠ¸ ë ì‹œê°„)
  const lastSegmentEndTime = Math.max(...originalTimings.map(t => t.endTime));
  const subtitleTotalDuration = lastSegmentEndTime;

  // ğŸ¯ ì„¸ê·¸ë¨¼íŠ¸ 6: ë¡œê³  (D+1ì´ˆ~D+2ì´ˆ)
  if (logoData) {
    const logoStartTime = subtitleTotalDuration + 1;  // D+1ì´ˆ
    const logoEndTime = logoStartTime + 1.0;          // D+2ì´ˆ (1ì´ˆ ê¸¸ì´)

    const logoSegment = {
      segmentId: 6,
      type: "animated_logo",
      id: logoData.id,
      name: logoData.name,
      mimeType: logoData.mimeType,
      url: logoData.url,
      source: "logo",
      timing: {
        startTime: logoStartTime,
        endTime: logoEndTime,
        duration: 1.0
      },
      text: "ë¡œê³  ì• ë‹ˆë©”ì´ì…˜ (ì¬ìƒ í›„ ì •ì§€)"
    };

    segments.push(logoSegment);
    console.log(`  âœ… ë¡œê³  ì„¸ê·¸ë¨¼íŠ¸ 6 ìƒì„±: ${logoData.name}`);
  }

  // === ğŸ¯ íƒ€ì´ë° ì¡°ì • ë¡œì§ (ê°€ì´ë“œ ì ìš©) ===
  const adjustedMatchedSegments = segments.map((seg) => ({ ...seg }));

  // 1. ì„¸ê·¸ë¨¼íŠ¸ 0ì˜ ì‹œì‘ ì‹œê°„ì€ í•­ìƒ 0ìœ¼ë¡œ ê³ ì •
  if (adjustedMatchedSegments.length > 0) {
    adjustedMatchedSegments[0].timing.startTime = 0;
  }

  // 2. ì´í›„ ì„¸ê·¸ë¨¼íŠ¸ë“¤ì˜ ì‹œì‘ì‹œê°„ì€ ìë§‰ì—ì„œ ë°›ì•„ì˜¨ ê°’ ì‚¬ìš©
  for (let i = 1; i < adjustedMatchedSegments.length; i++) {
    if (adjustedMatchedSegments[i].segmentId < 6) {
      // ì„¸ê·¸ë¨¼íŠ¸ 1~5ëŠ” ì›ë³¸ ìë§‰ ì‹œê°„ ìœ ì§€
      adjustedMatchedSegments[i].timing.startTime = segments[i].timing.startTime;
    }
    // ì„¸ê·¸ë¨¼íŠ¸ 6 (ë¡œê³ )ëŠ” ì´ë¯¸ ì˜¬ë°”ë¥¸ ì‹œê°„ìœ¼ë¡œ ì„¤ì •ë¨
  }

  // 3. ëª¨ë“  ì„¸ê·¸ë¨¼íŠ¸ì˜ endTimeê³¼ duration ê³„ì‚° (ë‹¤ìŒ ì„¸ê·¸ë¨¼íŠ¸ì˜ startTime ê¸°ì¤€)
  for (let i = 0; i < adjustedMatchedSegments.length - 1; i++) {
    const start = adjustedMatchedSegments[i].timing.startTime;
    const nextStart = adjustedMatchedSegments[i + 1].timing.startTime;
    adjustedMatchedSegments[i].timing.endTime = nextStart;
    adjustedMatchedSegments[i].timing.duration = parseFloat((nextStart - start).toFixed(6));
  }

  // 4. ë§ˆì§€ë§‰ ì„¸ê·¸ë¨¼íŠ¸ (ë¡œê³ )ì˜ durationì€ 1ì´ˆ ê³ ì •, endTimeë„ ì„¤ì •
  if (adjustedMatchedSegments.length > 0) {
    const last = adjustedMatchedSegments[adjustedMatchedSegments.length - 1];
    last.timing.endTime = parseFloat((last.timing.startTime + 1.0).toFixed(6));
    last.timing.duration = 1.0;
  }

  console.log('\nğŸ”§ íƒ€ì´ë° ì¡°ì • ì™„ë£Œ:');
  adjustedMatchedSegments.forEach((seg, i) => {
    console.log(`  ì„¸ê·¸ë¨¼íŠ¸ ${seg.segmentId}: ${seg.timing.startTime.toFixed(6)}~${seg.timing.endTime.toFixed(6)}ì´ˆ (${seg.timing.duration.toFixed(6)}ì´ˆ) - ${seg.name}`);
  });

  console.log(`\nâœ… matchedSegments ìƒì„± ì™„ë£Œ: ${adjustedMatchedSegments.length}ê°œ`);
  return adjustedMatchedSegments;
}

const matchedSegments = createMatchedSegments();

// === ìµœì¢… ê²°ê³¼ ìƒì„± ===
const result = {
  // ë¯¸ë””ì–´ íŒŒì¼ë“¤
  videoSegments: videoFiles,
  imageSegments: imageFiles,
  allSegments: [...videoFiles, ...imageFiles],

  // ğŸ”— í•µì‹¬: ì¡°ì •ëœ íƒ€ì´ë°ì´ ì ìš©ëœ matchedSegments
