// Google Drive URL ì •ê·œí™” í•¨ìˆ˜
function normalizeGoogleDriveUrl(url) {
  if (!url) return url;

  // /file/d/ íŒ¨í„´ì„ /uc?id= íŒ¨í„´ìœ¼ë¡œ ë³€í™˜
  if (url.includes('/file/d/')) {
    const fileIdMatch = url.match(/\/file\/d\/([a-zA-Z0-9_-]+)/);
    if (fileIdMatch) {
      const fileId = fileIdMatch[1];
      return `https://drive.google.com/uc?id=${fileId}&export=download`;
    }
  }

  // ì´ë¯¸ /uc?id= í˜•ì‹ì´ë©´ ê·¸ëŒ€ë¡œ ë°˜í™˜
  return url;
}

// FFmpeg ëª…ë ¹ì–´ ìƒì„± í•¨ìˆ˜
function generateFFmpegCommand() {
  console.log('ğŸ”§ FFmpeg ëª…ë ¹ì–´ ìƒì„± ì¤‘...');

  // ğŸ¯ í•µì‹¬ ìˆ˜ì •: ì´ ê¸¸ì´ë¥¼ ë§ˆì§€ë§‰ ì„¸ê·¸ë¨¼íŠ¸ì˜ endTimeìœ¼ë¡œ ê³„ì‚°
