  console.log(`ğŸ“ ì´ ì˜ìƒ ê¸¸ì´: ${totalDuration}ì´ˆ (ë§ˆì§€ë§‰ ì„¸ê·¸ë¨¼íŠ¸ endTime ê¸°ì¤€)`);

  // 1. ì…ë ¥ íŒŒì¼ë“¤
  let inputs = [];
  let inputIndex = 0;

  // ì„¸ê·¸ë¨¼íŠ¸ë³„ ì…ë ¥ ì²˜ë¦¬
  matchedSegments.forEach((segment, i) => {
    if (segment.type === 'generated_video') {
      // ë¹„ë””ì˜¤: ì´ë¯¸ ì†ë„ ì¡°ì •ëœ íŒŒì¼ ì‚¬ìš©
      inputs.push(`-i adjusted_segment_${i}.mp4`);
      console.log(`  ğŸ“¹ ì…ë ¥[${inputIndex}]: adjusted_segment_${i}.mp4 (ë¹„ë””ì˜¤)`);
    } else if (segment.type === 'generated_image') {
      // ì´ë¯¸ì§€: PNGë¡œ í†µì¼
      inputs.push(`-i segment_${i}.png`);
      console.log(`  ğŸ–¼ï¸  ì…ë ¥[${inputIndex}]: segment_${i}.png (ì´ë¯¸ì§€)`);
    } else if (segment.type === 'animated_logo') {
      // ğŸ¯ ìˆ˜ì •: ë¡œê³ ëŠ” 1ì´ˆë¡œ ì œí•œí•˜ë˜ ì „ì²´ íƒ€ì´ë°ì— í¬í•¨
      inputs.push(`-i segment_${i}.mp4`);
      console.log(`  ğŸ¯ ì…ë ¥[${inputIndex}]: segment_${i}.mp4 (ë¡œê³ )`);
    }
    inputIndex++;
  });

  // ğŸ¯ ìˆ˜ì •: ì˜¤ë””ì˜¤ íŒŒì¼ë“¤ - ì›ë³¸ íŒŒì¼ëª… ì‚¬ìš©
  const narrationFileName = narrationAudio ? narrationAudio.name : 'narration.mp3';
  const bgmFileName = backgroundMusic ? backgroundMusic.name : 'bgm.mp3';

  inputs.push(`-i "${narrationFileName}"`);
  inputs.push(`-i "${bgmFileName}"`);
  console.log(`  ğŸ§ ì…ë ¥[${inputIndex}]: ${narrationFileName} (ë‚˜ë ˆì´ì…˜)`);
  console.log(`  ğŸµ ì…ë ¥[${inputIndex + 1}]: ${bgmFileName} (BGM)`);

  const narrationIndex = inputIndex;
  const bgmIndex = inputIndex + 1;

  // 2. í•„í„° ë³µí•©ì²´ ìƒì„± (60fps í†µì¼ ì ìš©)
  let filterComplex = [];
  let videoLabels = [];

  matchedSegments.forEach((segment, i) => {
    const videoLabel = `v${i}`;
    videoLabels.push(`[${videoLabel}]`);

    if (segment.type === 'generated_video') {
      // ë¹„ë””ì˜¤: ì„¸ë¡œ ë³€í™˜ + 60fps ì„¤ì •
      filterComplex.push(
        `[${i}:v]scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2:color=black,setsar=1,fps=60[${videoLabel}]`
      );
      console.log(`  ğŸ¬ ì„¸ê·¸ë¨¼íŠ¸ ${i}: ë¹„ë””ì˜¤ ì²˜ë¦¬ (60fps, ${segment.timing.duration}ì´ˆ)`);

    } else if (segment.type === 'generated_image') {
      // ì´ë¯¸ì§€: ì¼„ë²ˆ íš¨ê³¼ + 60fps í†µì¼
      const timing = segment.timing;
      const frameCount = Math.round(timing.duration * 60); // 60fps ê¸°ì¤€ í”„ë ˆì„ìˆ˜

      // ì¼„ë²ˆ íš¨ê³¼ë³„ ì„¤ì •
      const kenBurnsEffects = [
        { // ì„¸ê·¸ë¨¼íŠ¸ 2: Safety Sweep L2R
          filter: `zoompan=z='1.40-0.40*(on/${frameCount})':d=${frameCount}:x='(iw-iw/zoom)*(0.1+0.6*(on/${frameCount}))':y='(ih-ih/zoom)/2':s=1080x1920:fps=60`
        },
        { // ì„¸ê·¸ë¨¼íŠ¸ 3: Diagonal Drift  
          filter: `zoompan=z='1+0.45*(on/${frameCount})':d=${frameCount}:x='(iw-iw/zoom)*(0.10+0.60*(on/${frameCount}))':y='(ih-ih/zoom)*(0.10+0.60*(on/${frameCount}))+0.03*(ih-ih/zoom)*sin(PI*on/${frameCount})':s=1080x1920:fps=60`
        },
        { // ì„¸ê·¸ë¨¼íŠ¸ 4: One-Bounce Tilt
          filter: `zoompan=z='1+0.50*(on/${frameCount})':d=${frameCount}:x='(iw-iw/zoom)/2':y='(ih-ih/zoom)*(0.50-0.30*(on/${frameCount})+0.05*sin(PI*on/${frameCount}))':s=1080x1920:fps=60`
        },
        { // ì„¸ê·¸ë¨¼íŠ¸ 5: Wide Sweep L2R
          filter: `zoompan=z='1.3-0.2*(on/${frameCount})':d=${frameCount}:x='iw*0.05+iw*0.9*on/${frameCount}':y='ih/2-(ih*zoom)/2':s=1080x1920:fps=60`
        }
      ];

      // ì´ë¯¸ì§€ ì„¸ê·¸ë¨¼íŠ¸ ì¸ë±ìŠ¤ ê³„ì‚° (ë¹„ë””ì˜¤ ì„¸ê·¸ë¨¼íŠ¸ ì œì™¸)
      const imageSegmentIndex = matchedSegments.slice(0, i).filter(s => s.type === 'generated_image').length;
      const kenBurnsEffect = kenBurnsEffects[imageSegmentIndex] || kenBurnsEffects[0];

      filterComplex.push(
        `[${i}:v]scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2:color=black,setsar=1,${kenBurnsEffect.filter}[${videoLabel}]`
      );
      console.log(`  ğŸ¨ ì„¸ê·¸ë¨¼íŠ¸ ${i}: ì¼„ë²ˆ íš¨ê³¼ (${timing.duration}ì´ˆ, ${frameCount}í”„ë ˆì„, 60fps)`);

    } else if (segment.type === 'animated_logo') {
      // ğŸ¯ ìˆ˜ì •: ë¡œê³  ì²˜ë¦¬ - 1ì´ˆ duration ì ìš©í•˜ë˜ 60fpsë¡œ ì²˜ë¦¬
      const logoDuration = segment.timing.duration; // 1ì´ˆ
      const logoFrameCount = Math.round(logoDuration * 60);

      filterComplex.push(
        `[${i}:v]scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2:color=black,setsar=1,fps=60,trim=duration=${logoDuration}[${videoLabel}]`
      );
      console.log(`  ğŸ¯ ì„¸ê·¸ë¨¼íŠ¸ ${i}: ë¡œê³  ì²˜ë¦¬ (${logoDuration}ì´ˆ, ${logoFrameCount}í”„ë ˆì„, 60fps)`);
    }
  });

  // 3. ë¹„ë””ì˜¤ íƒ€ì„ë¼ì¸ ì—°ê²° - ğŸ¯ ìˆ˜ì •: ëª¨ë“  ì„¸ê·¸ë¨¼íŠ¸ í¬í•¨
  filterComplex.push(`${videoLabels.join('')}concat=n=${matchedSegments.length}:v=1:a=0[video_timeline]`);
  console.log(`  ğŸ”— concat: ${matchedSegments.length}ê°œ ì„¸ê·¸ë¨¼íŠ¸ ì—°ê²°`);

  // 4. ìë§‰ ì¶”ê°€
  filterComplex.push(`[video_timeline]ass=subtitle.ass[final_video]`);

  // 5. ì˜¤ë””ì˜¤ ì²˜ë¦¬ - ğŸ¯ ìˆ˜ì •: ë™ì  ì´ ê¸¸ì´ ì‚¬ìš©
  const fadeOutTime = totalDuration - 1;

  filterComplex.push(
    `[${bgmIndex}:a]volume=0.15,afade=t=in:st=0:d=1,afade=t=out:st=${fadeOutTime}:d=1,apad=pad_dur=${totalDuration}[bgm_faded]`
  );
  filterComplex.push(
    `[${narrationIndex}:a]afade=t=out:st=${fadeOutTime}:d=1,apad=pad_dur=${totalDuration}[narration_padded]`
  );
  filterComplex.push(
    `[narration_padded][bgm_faded]amix=inputs=2:duration=longest[final_audio]`
  );

  // 6. ìµœì¢… FFmpeg ëª…ë ¹ì–´ ì¡°ë¦½ - ğŸ¯ ìˆ˜ì •: ë™ì  ì´ ê¸¸ì´ ì ìš©
  const ffmpegCommand = [
    'ffmpeg -y',
    inputs.join(' '),
    `-filter_complex "${filterComplex.join(';')}"`,
    '-map [final_video]',
    '-map [final_audio]',
    '-c:v libx264',
    '-preset medium',
    '-crf 18',
    '-profile:v high',
    '-level:v 4.0',
    '-pix_fmt yuv420p',
    '-r 60', // 60fps ì¶œë ¥ ìœ ì§€
    '-s 1080x1920',
    '-c:a aac',
    '-b:a 320k',
    '-ar 48000',
    `-t ${totalDuration}`, // ğŸ¯ í•µì‹¬ ìˆ˜ì •: ë™ì  ì´ ê¸¸ì´ ì‚¬ìš©
    '-movflags +faststart',
    '-avoid_negative_ts make_zero',
    'output_final_video.mp4'
  ].join(' ');

  return ffmpegCommand;
}

// ë‹¤ìš´ë¡œë“œ ëª…ë ¹ì–´ ìƒì„±
function generateDownloadCommands() {
  const commands = [];

  matchedSegments.forEach((segment, i) => {
    const normalizedUrl = normalizeGoogleDriveUrl(segment.url);

    if (segment.type === 'generated_image') {
      commands.push(`wget -O "segment_${i}.png" "${normalizedUrl}"`);
    } else if (segment.type === 'generated_video') {
      commands.push(`wget -O "segment_${i}.mp4" "${normalizedUrl}"`);
    } else if (segment.type === 'animated_logo') {
      commands.push(`wget -O "segment_${i}.mp4" "${normalizedUrl}"`);
    }
  });

  // ğŸ¯ ìˆ˜ì •: ë‚˜ë ˆì´ì…˜ ì˜¤ë””ì˜¤ë¥¼ ì›ë³¸ íŒŒì¼ëª…ìœ¼ë¡œ ì €ì¥
  if (narrationAudio) {
    const normalizedNarrationUrl = normalizeGoogleDriveUrl(narrationAudio.url);
    const narrationFileName = narrationAudio.name; // ì›ë³¸ íŒŒì¼ëª… ì‚¬ìš©
    commands.push(`wget -O "${narrationFileName}" "${normalizedNarrationUrl}"`);
  }

  // ğŸ¯ ìˆ˜ì •: BGMë„ ì›ë³¸ íŒŒì¼ëª…ìœ¼ë¡œ ì €ì¥
  if (backgroundMusic) {
    const normalizedBgmUrl = normalizeGoogleDriveUrl(backgroundMusic.url);
    const bgmFileName = backgroundMusic.name; // ì›ë³¸ íŒŒì¼ëª… ì‚¬ìš©
    commands.push(`wget -O "${bgmFileName}" "${normalizedBgmUrl}"`);
  }

  return commands;
}

