  }

  let script = '\n# ì˜ìƒ ì„¸ê·¸ë¨¼íŠ¸ë“¤ ë™ì  ì†ë„ ì¡°ì •\n';

  videoSegments.forEach((segment, index) => {
    const segmentIndex = matchedSegments.findIndex(s => s.segmentId === segment.segmentId);
    const targetDuration = segment.timing.duration;

    script += `
# ì„¸ê·¸ë¨¼íŠ¸ ${segmentIndex} ë™ì  ì†ë„ ì¡°ì •
ORIGINAL_DURATION_${segmentIndex}=$(ffprobe -v quiet -show_entries format=duration -of csv=p=0 segment_${segmentIndex}.mp4)
TARGET_DURATION_${segmentIndex}=${targetDuration}
SPEED_FACTOR_${segmentIndex}=$(awk -v o="$ORIGINAL_DURATION_${segmentIndex}" -v d="$TARGET_DURATION_${segmentIndex}" 'BEGIN{printf "%.6f", d/o}')
echo "ì„¸ê·¸ë¨¼íŠ¸ ${segmentIndex}: ì›ë³¸ \${ORIGINAL_DURATION_${segmentIndex}}ì´ˆ â†’ ëª©í‘œ ${targetDuration}ì´ˆ (SPEED_FACTOR: \${SPEED_FACTOR_${segmentIndex}})"

ffmpeg -y -i "segment_${segmentIndex}.mp4" \\
  -vf "scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2:color=black,setsar=1,setpts=\${SPEED_FACTOR_${segmentIndex}}*PTS,fps=60" \\
  -c:v libx264 -preset fast -crf 23 -pix_fmt yuv420p -an \\
  "adjusted_segment_${segmentIndex}.mp4"
echo "âœ… adjusted_segment_${segmentIndex}.mp4 ìƒì„± ì™„ë£Œ"
`;
  });

  return script;
}

