function smartSentenceSplit(text) {
  console.log('ğŸ” ìŠ¤ë§ˆíŠ¸ ë¬¸ì¥ ë¶„ë¦¬ ì‹œì‘...');
  console.log('ğŸ”¤ ì›ë³¸ í…ìŠ¤íŠ¸:', text);

