  console.log(`ğŸ“Š ì´ ${basicSentences.length}ê°œ ë¬¸ì¥ ë¶„ë¦¬ ì™„ë£Œ`);
  return basicSentences.filter(s => s.length > 0);
}

// ì•½ì–´/ì´ë‹ˆì…œ íŒë‹¨ í•¨ìˆ˜ (ë” ì—„ê²©í•˜ê²Œ ìˆ˜ì •)
function checkIfAbbreviation(text, dotIndex, prevChar, nextChar) {
  // 1. ë‹¨ì¼ ëŒ€ë¬¸ì + ë§ˆì¹¨í‘œ íŒ¨í„´ë§Œ ì•½ì–´ë¡œ ì¸ì • (ì˜ˆ: "L.")
  if (prevChar && prevChar.match(/[A-Z]/) && nextChar === ' ') {
    const wordBefore = getWordBefore(text, dotIndex - 1);
    const nextWord = getNextWord(text, dotIndex + 1);

    // ë‹¨ì¼ ë¬¸ìì´ê³ , ë‹¤ìŒ ë‹¨ì–´ê°€ ì†Œë¬¸ìë‚˜ í•œê¸€ë¡œ ì‹œì‘í•˜ë©´ ì•½ì–´
    if (wordBefore.length === 1 && nextWord && 
        (nextWord[0].match(/[a-zê°€-í£]/) || nextWord[0] === nextWord[0].toLowerCase())) {
      console.log('ğŸ”¤ ë‹¨ì¼ ëŒ€ë¬¸ì ì•½ì–´ ê°ì§€:', `"${wordBefore}." â†’ "${nextWord}"`);
      return true;
    }
  }

  // 2. ìˆ«ì + % + ë‚˜ íŒ¨í„´ (ì˜ˆ: "16%ë‚˜")ì€ ì•½ì–´ê°€ ì•„ë‹˜
  if (prevChar && prevChar.match(/[%ë‚˜ë‹¤ìŠµë‹ˆ]/)) {
    return false;
  }

  // 3. ì¼ë°˜ì ì¸ í•œêµ­ì–´ ì–´ë¯¸ëŠ” ì•½ì–´ê°€ ì•„ë‹˜
  const wordBefore = getWordBefore(text, dotIndex - 1);
  if (wordBefore.match(/ìŠµë‹ˆë‹¤|ë©ë‹ˆë‹¤|ì…ë‹ˆë‹¤$/)) {
    return false;
  }

  return false;
}

// ì´ì „ ë‹¨ì–´ ì¶”ì¶œ
function getWordBefore(text, index) {
  let word = '';
  for (let i = index; i >= 0; i--) {
    const char = text[i];
    if (char === ' ') break;
    word = char + word;
  }
  return word;
}

// ë‹¤ìŒ ë‹¨ì–´ ì¶”ì¶œ
function getNextWord(text, startIndex) {
  let word = '';
  let foundStart = false;

  for (let i = startIndex; i < text.length; i++) {
    const char = text[i];

    if (!foundStart && char === ' ') continue;
    if (!foundStart && char !== ' ') foundStart = true;

    if (foundStart) {
      if (char === ' ') break;
      word += char;
    }
  }

  return word;
}

