function findWordsForSentence(sentence, allWords, startIndex = 0) {
  const sentenceWords = sentence.split(/\s+/).filter(w => w.length > 0);
  const matchedWords = [];
  let wordIndex = startIndex;
  let sentenceWordIndex = 0;

  console.log(`ğŸ” ë¬¸ì¥ ì²˜ë¦¬: "${sentence}"`);
  console.log(`   ì°¾ì„ ë‹¨ì–´ë“¤: [${sentenceWords.join(', ')}]`);

  // ê° ë¬¸ì¥ì˜ ì²« ë²ˆì§¸ ë‹¨ì–´ë¶€í„° ë§ˆì§€ë§‰ ë‹¨ì–´ê¹Œì§€ ìˆœì°¨ì ìœ¼ë¡œ ì°¾ê¸°
  while (wordIndex < allWords.length && sentenceWordIndex < sentenceWords.length) {
    const currentWord = allWords[wordIndex].word.trim();
    const targetWord = sentenceWords[sentenceWordIndex];

    // ë‹¨ì–´ê°€ ë§¤ì¹­ë˜ëŠ”ì§€ í™•ì¸ (ë¶€ë¶„ ë§¤ì¹­ í—ˆìš©)
    if (currentWord.includes(targetWord) || targetWord.includes(currentWord) || 
        currentWord.replace(/[.,!?]/g, '') === targetWord.replace(/[.,!?]/g, '')) {
      matchedWords.push(allWords[wordIndex]);
      console.log(`   âœ… ë§¤ì¹­: "${targetWord}" = "${currentWord}" (${allWords[wordIndex].start.toFixed(2)}s)`);
      sentenceWordIndex++;
    } else {
      console.log(`   â­ï¸ ìŠ¤í‚µ: "${currentWord}" (${allWords[wordIndex].start.toFixed(2)}s)`);
    }
    wordIndex++;
  }

  console.log(`   ê²°ê³¼: ${matchedWords.length}/${sentenceWords.length} ë‹¨ì–´ ë§¤ì¹­`);

  return { matchedWords, nextStartIndex: wordIndex };
}

