function generateEnhancedASS(words) {
  const assHeader = `[Script Info]
Title: Netflix AI News - Enhanced Electric Blue
ScriptType: v4.00+

[V4+ Styles]
Format: Name, Fontname, Fontsize, PrimaryColour, SecondaryColour, OutlineColour, BackColour, Bold, Italic, Underline, StrikeOut, ScaleX, ScaleY, Spacing, Angle, BorderStyle, Outline, Shadow, Alignment, MarginL, MarginR, MarginV, Encoding
Style: ElectricGlow,Montserrat,22,&Hffffff,&Hffffff,&H00FF8000,&H80000000,1,0,0,0,100,100,0,0,1,4,6,5,50,50,960,1
Style: PunchEffect,Montserrat,22,&Hffffff,&Hffffff,&H00FF8000,&H80000000,1,0,0,0,100,100,0,0,1,4,6,5,50,50,960,1

[Events]
Format: Layer, Start, End, Style, Name, MarginL, MarginR, MarginV, Effect, Text
`;

  let assEvents = '';

  words.forEach((word, index) => {
    const startTime = formatTimeForASS(word.start * 1000);
    const endTime = formatTimeForASS(word.end * 1000);

    // êµëŒ€ë¡œ ë‹¤ë¥¸ ì• ë‹ˆë©”ì´ì…˜ ì ìš©
    const useGlowEffect = index % 2 === 0;

    if (useGlowEffect) {
      const animation = `{\\fad(300,200)\\t(0,300,\\fscx110\\fscy110\\3c&H00FF8000&\\bord5)\\t(300,600,\\fscx100\\fscy100\\3c&H00CC6000&\\bord4)\\t(600,900,\\3c&H00FF8000&\\bord5)}`;
      assEvents += `Dialogue: 0,${startTime},${endTime},ElectricGlow,,0,0,0,,${animation}${word.word}\n`;
    } else {
      const animation = `{\\fad(200,200)\\t(0,200,\\fscx120\\fscy120\\frz3\\3c&H00FF8000&\\bord6)\\t(200,400,\\fscx100\\fscy100\\frz0\\3c&H00CC6000&\\bord4)\\t(400,600,\\3c&H00FF8000&\\bord5)}`;
      assEvents += `Dialogue: 0,${startTime},${endTime},PunchEffect,,0,0,0,,${animation}${word.word}\n`;
    }
  });

  return assHeader + assEvents;
}

// ASS ì‹œê°„ í˜•ì‹ ë³€í™˜
function formatTimeForASS(ms) {
  const hours = Math.floor(ms / 3600000);
  const minutes = Math.floor((ms % 3600000) / 60000);
  const seconds = Math.floor((ms % 60000) / 1000);
  const centiseconds = Math.floor((ms % 1000) / 10);

  return `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}.${centiseconds.toString().padStart(2, '0')}`;
}

// SRT í˜•ì‹ ìƒì„±
function generateSRT(words) {
  let srtContent = '';

  words.forEach((word, index) => {
    const startTime = formatSecondsToSRT(word.start);
    const endTime = formatSecondsToSRT(word.end);

    srtContent += `${index + 1}\n`;
    srtContent += `${startTime} --> ${endTime}\n`;
    srtContent += `${word.word}\n\n`;
  });

  return srtContent.trim();
}

// SRT ì‹œê°„ í˜•ì‹ ë³€í™˜
function formatSecondsToSRT(seconds) {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = Math.floor(seconds % 60);
  const ms = Math.floor((seconds % 1) * 1000);

  return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')},${ms.toString().padStart(3, '0')}`;
}

// WebVTT í˜•ì‹ ìƒì„±
function generateWebVTT(words) {
  let webVttContent = 'WEBVTT\n\n';

  words.forEach((word, index) => {
    const startTime = formatSecondsToWebVTT(word.start);
    const endTime = formatSecondsToWebVTT(word.end);

    webVttContent += `${index + 1}\n`;
    webVttContent += `${startTime} --> ${endTime}\n`;
    webVttContent += `${word.word}\n\n`;
  });

  return webVttContent.trim();
}

// WebVTT ì‹œê°„ í˜•ì‹ ë³€í™˜
function formatSecondsToWebVTT(seconds) {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = seconds % 60;

  return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toFixed(3).padStart(6, '0')}`;
}

